<?phpfunction mandar_email($nome, $email, $mensagemHTML, $assunto){//if(!isset($_POST[Submit])) die("N&atilde;o recebi nenhum par&acitc;metro. Por favor volte ao formulario.html antes");    /* Medida preventiva para evitar que outros domínios sejam remetente da sua mensagem. */    if (eregi('tempsite.ws$|locaweb.com.br$|hospedagemdesites.ws$|websiteseguro.com$', $_SERVER[HTTP_HOST])) {        $emailsender = 'contato@camarasjc2.hospedagemdesites.ws';    } else {        $emailsender = "contato@" . $_SERVER[HTTP_HOST];        //    Na linha acima estamos forçando que o remetente seja 'webmaster@seudominio',        // você pode alterar para que o remetente seja, por exemplo, 'contato@seudominio'.    }    /* Verifica qual é o sistema operacional do servidor para ajustar o cabeçalho de forma correta. Não alterar */    if (PHP_OS == "Linux") $quebra_linha = "\n"; //Se for Linux    elseif (PHP_OS == "WINNT") $quebra_linha = "\r\n"; // Se for Windows    else die("Este script nao esta preparado para funcionar com o sistema operacional de seu servidor");    $name = $nome;    $emaildestinatario = $email;    $msg = $mensagem;    //$comcopia = 'lucas.sandes@camarasjc.sp.gov.br';    /* Montando o cabeçalho da mensagem */    $headers = "MIME-Version: 1.1" . $quebra_linha;    $headers .= "Content-type: text/html; charset=UTF-8" . $quebra_linha;    // Perceba que a linha acima contém "text/html", sem essa linha, a mensagem não chegará formatada.    $headers .= "From: Contato Camara <" . $emailsender . ">" . $quebra_linha;    $headers .= "Return-Path: " . $emailsender . $quebra_linha;    // Esses dois "if's" abaixo são porque o Postfix obriga que se um cabeçalho for especificado, deverá haver um valor.    // Se não houver um valor, o item não deverá ser especificado.    //$headers .= "Cc: " . $comcopia . $quebra_linha;    $headers .= "Reply-To: " . $emailsender . $quebra_linha;    // Note que o e-mail do remetente será usado no campo Reply-To (Responder Para)    /* Enviando a mensagem */    if (mail($emaildestinatario, $assunto, $mensagemHTML, $headers, "-r" . $emailsender)) {        return true;    } else {        return false;    }}