asia").attr("disabled", false);            $("#nomeContato").attr("disabled", false);            $("#cnpj").attr("disabled", false);            $("#inscEstadual").attr("disabled", false);            $("#nomePessoal").attr("disabled", true);            $("#cpf").attr("disabled", true);            $("#nomePessoal").attr("required", false);            $("#cpf").attr("required", false);            $("#razaoSocial").attr("required", true);            $("#nomeFantasia").attr("required", true);            $("#nomeContato").attr("required", true);            $("#cnpj").attr("required", true);            $("#inscEstadual").attr("required", true);            $(".dadosPessoaJuridica").show();            $(".dadosPessoaFisica").hide();        }    }</script></body></html>